package wrapper;

public class Primitives_data {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Integer Range : ");
		System.out.println("Max_range : " + Integer.MAX_VALUE);
		System.out.println("Min_range : " + Integer.MIN_VALUE);
		System.out.println("Double Range : ");
		System.out.println("Max_range : " + Double.MAX_VALUE);
		System.out.println("Min_range : " + Double.MIN_VALUE);
		System.out.println("Short Range : ");
		System.out.println("Max_range : " + Short.MAX_VALUE);
		System.out.println("Min_range : " + Short.MIN_VALUE);
		System.out.println("Float Range : ");
		System.out.println("Max_range : " + Float.MAX_VALUE);
		System.out.println("Min_range : " + Float.MIN_VALUE);
		System.out.println("Byte Range : ");
		System.out.println("Max_range : " + Byte.MAX_VALUE);
		System.out.println("Min_range : " + Byte.MIN_VALUE);
		System.out.println("Long Range : ");
		System.out.println("Max_range : " + Long.MAX_VALUE);
		System.out.println("Min_range : " + Long.MIN_VALUE);

	}

}
